PROC OPTIONS(MAIN);
  DCL X FIXED BIN(31);
  DCL Y FIXED BIN(31);

  X = 10;
  Y = 20;
  PUT SKIP LIST('Sum:', X + Y);
ENDPROC;

PROC OPTIONS(MAIN);
  DCL ARR(10) FIXED BIN(31);
  DCL I FIXED BIN(31);

  DO I = 1 TO 10;
    ARR(I) = I * 2;
  END;
ENDPROC;

PROC OPTIONS(MAIN);
  DCL STR CHAR(50) VAR;
  DCL LEN FIXED BIN(31);

  STR = 'Hello, PL/I!';
  LEN = LENGTH(STR);
  PUT SKIP LIST('Length:', LEN);
ENDPROC;
